<boltAction type="file" filePath="script.js">document.addEventListener('DOMContentLoaded', () => {
    // --- GLOBAL STATE & CONFIG ---
    const GOOGLE_API_SCOPES = 'https://www.googleapis.com/auth/blogger https://www.googleapis.com/auth/cloud-platform';
    let tokenClient;
    let accessToken = null;
    let automationTimeoutId = null;
    let isAutomationRunning = false;
    let isGisLoaded = false;
    let postingHistory = [];
    let currentPromptItem = null; // 현재 처리 중인 프롬프트 DOM 요소 또는 null (개별 포스팅)
    let uploadedImageUri = null; // 사용자가 업로드한 이미지의 Data URI
    let shutdownCountdownInterval = null;

    // --- DOM ELEMENTS ---
    const apiKeyInput = document.getElementById('apiKey');
    const clientIdInput = document.getElementById('clientId');
    const blogIdInput = document.getElementById('blogId');
    const blogAddressInput = document.getElementById('blogAddress');
    const loginBtn = document.getElementById('loginBtn');
    const authStatus = document.getElementById('authStatus');

    const promptContainer = document.getElementById('promptContainer');
    const addPromptBtn = document.getElementById('addPromptBtn');
    const locationInput = document.getElementById('location');
    const intervalSelect = document.getElementById('interval');
    const automationBtn = document.getElementById('automationBtn');

    const logContainer = document.getElementById('logContainer');
    const themeToggleBtn = document.getElementById('themeToggleBtn');

    const historyList = document.getElementById('historyList');
    const clearHistoryBtn = document.getElementById('clearHistoryBtn');

    const imageSourceRadios = document.querySelectorAll('input[name="imageSource"]');
    const pexelsConfig = document.getElementById('pexelsConfig');
    const pexelsApiKeyInput = document.getElementById('pexelsApiKey');
    const aiImageConfig = document.getElementById('aiImageConfig');
    const gcpProjectIdInput = document.getElementById('gcpProjectId');
    const uploadConfig = document.getElementById('uploadConfig');
    const userImageUpload = document.getElementById('userImageUpload');
    const imagePreviewContainer = document.getElementById('imagePreviewContainer');
    const imagePreview = document.getElementById('imagePreview');
    const removeImageBtn = document.getElementById('removeImageBtn');

    const geminiModelSelect = document.getElementById('geminiModel');

    const writingToneSelect = document.getElementById('writingTone');
    const targetAudienceInput = document.getElementById('targetAudience');
    const postAsDraftCheckbox = document.getElementById('postAsDraft');
    const previewModal = document.getElementById('previewModal');
    const closeModalBtn = document.getElementById('closeModalBtn');
    const cancelPostBtn = document.getElementById('cancelPostBtn');
    const approvePostBtn = document.getElementById('approvePostBtn');
    const previewTitleInput = document.getElementById('previewTitle');
    const previewBodyTextarea = document.getElementById('previewBody');

    // YouTube Feature Elements
    const youtubeUrlContainer = document.getElementById('youtubeUrlContainer');
    const addYoutubeUrlBtn = document.getElementById('addYoutubeUrlBtn');
    const generateFromYoutubeBtn = document.getElementById('generateFromYoutubeBtn');

    // Auto Shutdown Elements
    const autoShutdownCheckbox = document.getElementById('autoShutdown');
    const shutdownStatusContainer = document.getElementById('shutdownStatusContainer');
    const shutdownStatusText = document.getElementById('shutdownStatusText');
    const cancelShutdownBtn = document.getElementById('cancelShutdownBtn');

    // --- INITIALIZATION ---
    function loadGisScript() {
        const script = document.createElement('script');
        script.src = 'https://accounts.google.com/gsi/client';
        script.async = true;
        script.defer = true;
        script.onload = () => { isGisLoaded = true; console.log('Google GIS client loaded.'); };
        script.onerror = () => { console.error('Failed to load Google GIS client.'); };
        document.head.appendChild(script);
    }

    // --- EVENT LISTENERS ---
    loginBtn.addEventListener('click', handleAuthClick);
    automationBtn.addEventListener('click', handleAutomationToggle);
    addPromptBtn.addEventListener('click', addPromptInput);
    promptContainer.addEventListener('click', handlePromptContainerClick);
    themeToggleBtn.addEventListener('click', toggleTheme);
    clearHistoryBtn.addEventListener('click', handleClearHistory);
    imageSourceRadios.forEach(radio => radio.addEventListener('change', handleImageSourceChange));

    userImageUpload.addEventListener('change', handleImageUpload);
    removeImageBtn.addEventListener('click', handleRemoveImage);

    closeModalBtn.addEventListener('click', handleCancelPost);
    cancelPostBtn.addEventListener('click', handleCancelPost);
    approvePostBtn.addEventListener('click', handleApprovePost);

    // YouTube Listeners
    addYoutubeUrlBtn.addEventListener('click', addYoutubeUrlInput);
    youtubeUrlContainer.addEventListener('click', handleYoutubeUrlContainerClick);
    generateFromYoutubeBtn.addEventListener('click', handleGenerateFromYoutube);

    // Auto Shutdown Listener
    cancelShutdownBtn.addEventListener('click', cancelShutdown);


    // --- 설정 값 저장 및 불러오기 ---
    const settingsToPersist = {
        apiKey: apiKeyInput,
        clientId: clientIdInput,
        blogId: blogIdInput,
        blogAddress: blogAddressInput,
        pexelsApiKey: pexelsApiKeyInput,
        gcpProjectId: gcpProjectIdInput,
        location: locationInput,
        geminiModel: geminiModelSelect,
        writingTone: writingToneSelect,
        targetAudience: targetAudienceInput,
        postAsDraft: {
            get: () => postAsDraftCheckbox.checked,
            set: (value) => { postAsDraftCheckbox.checked = value; }
        },
        autoShutdown: {
            get: () => autoShutdownCheckbox.checked,
            set: (value) => { autoShutdownCheckbox.checked = value; }
        }
    };

    function saveSettings() {
        const settings = {};
        for (const key in settingsToPersist) {
            settings[key] = typeof settingsToPersist[key].get === 'function' ? settingsToPersist[key].get() : settingsToPersist[key].value;
        }
        localStorage.setItem('autoBloggerSettings', JSON.stringify(settings));
    }

    function loadSettings() {
        const savedSettings = localStorage.getItem('autoBloggerSettings');
        if (savedSettings) {
            const settings = JSON.parse(savedSettings);
            for (const key in settings) {
                if (settingsToPersist[key] && settings[key] !== null && settings[key] !== undefined) {
                    if (typeof settingsToPersist[key].set === 'function') {
                        settingsToPersist[key].set(settings[key]);
                    } else {
                        settingsToPersist[key].value = settings[key];
                    }
                }
            }
            const savedImageSource = localStorage.getItem('autoBloggerImageSource');
            if (savedImageSource) {
                const radioToSelect = document.querySelector(`input[name="imageSource"][value="${savedImageSource}"]`);
                if(radioToSelect) {
                    radioToSelect.checked = true;
                    handleImageSourceChange();
                }
            }
            logMessage('⚙️', '저장된 설정을 불러왔습니다.');
        }
    }

    Object.values(settingsToPersist).forEach(item => {
        const element = item.get ? (item === settingsToPersist.postAsDraft ? postAsDraftCheckbox : autoShutdownCheckbox) : item;
        element.addEventListener('input', saveSettings);
        element.addEventListener('change', saveSettings);
    });

    imageSourceRadios.forEach(radio => {
        radio.addEventListener('change', (e) => {
            localStorage.setItem('autoBloggerImageSource', e.target.value);
            handleImageSourceChange();
        });
    });


    // --- THEME SWITCHER ---
    function setInitialTheme() {
        const savedTheme = localStorage.getItem('theme') || 'light';
        document.body.setAttribute('data-theme', savedTheme);
        themeToggleBtn.querySelector('.icon-sun').style.display = savedTheme === 'dark' ? 'none' : 'block';
        themeToggleBtn.querySelector('.icon-moon').style.display = savedTheme === 'dark' ? 'block' : 'none';
    }

    function toggleTheme() {
        const currentTheme = document.body.getAttribute('data-theme');
        const newTheme = currentTheme === 'dark' ? 'light' : 'dark';
        document.body.setAttribute('data-theme', newTheme);
        localStorage.setItem('theme', newTheme);
        themeToggleBtn.querySelector('.icon-sun').style.display = newTheme === 'dark' ? 'none' : 'block';
        themeToggleBtn.querySelector('.icon-moon').style.display = newTheme === 'dark' ? 'block' : 'none';
    }

    // --- IMAGE SOURCE SELECTION & UPLOAD ---
    function handleImageSourceChange() {
        const selectedSource = document.querySelector('input[name="imageSource"]:checked').value;
        pexelsConfig.classList.toggle('hidden', selectedSource !== 'pexels');
        aiImageConfig.classList.toggle('hidden', selectedSource !== 'ai');
        uploadConfig.classList.toggle('hidden', selectedSource !== 'upload');
    }

    function handleImageUpload(event) {
        const file = event.target.files[0];
        if (!file) return;

        const reader = new FileReader();
        reader.onload = (e) => {
            uploadedImageUri = e.target.result;
            imagePreview.src = uploadedImageUri;
            imagePreviewContainer.classList.remove('hidden');
        };
        reader.readAsDataURL(file);
    }

    function handleRemoveImage() {
        uploadedImageUri = null;
        userImageUpload.value = ''; // Clear the file input
        imagePreview.src = '#';
        imagePreviewContainer.classList.add('hidden');
    }

    // --- PROMPT MANAGEMENT ---
    function addPromptInput() {
        const promptItem = document.createElement('div');
        promptItem.className = 'prompt-item';
        const input = document.createElement('input');
        input.type = 'text';
        input.className = 'prompt-input';
        input.placeholder = `추가 프롬프트`;
        const removeBtn = document.createElement('button');
        removeBtn.className = 'remove-prompt-btn';
        removeBtn.innerHTML = '×';
        promptItem.appendChild(input);
        promptItem.appendChild(removeBtn);
        promptContainer.appendChild(promptItem);
    }

    function handlePromptContainerClick(event) {
        if (event.target.classList.contains('remove-prompt-btn')) {
            if (promptContainer.children.length > 1) {
                event.target.closest('.prompt-item').remove();
            } else {
                alert('최소 하나 이상의 프롬프트가 필요합니다.');
            }
        }
    }

    // --- AUTHENTICATION ---
    function handleAuthClick() {
        if (!isGisLoaded) {
            updateAuthStatus('error', 'Google 인증 라이브러리가 아직 로드되지 않았습니다.');
            return;
        }
        const clientId = clientIdInput.value.trim();
        if (!clientId) {
            updateAuthStatus('error', 'OAuth 2.0 클라이언트 ID를 먼저 입력해주세요.');
            return;
        }
        if (accessToken) {
            google.accounts.oauth2.revoke(accessToken, () => {
                accessToken = null;
                updateAuthStatus('logged_out');
            });
        } else {
            tokenClient = window.google.accounts.oauth2.initTokenClient({
                client_id: clientId,
                scope: GOOGLE_API_SCOPES,
                callback: handleTokenResponse,
            });
            tokenClient.requestAccessToken({ prompt: 'consent' });
        }
    }

    function handleTokenResponse(response) {
        if (response.error) {
            updateAuthStatus('error', `인증 오류: ${response.error}`);
            return;
        }
        accessToken = response.access_token;
        updateAuthStatus('logged_in');
    }

    function updateAuthStatus(status, message = '') {
        authStatus.classList.remove('hidden', 'success', 'error');
        switch (status) {
            case 'logged_in':
                authStatus.classList.add('success');
                authStatus.textContent = '✅ Google 계정에 성공적으로 로그인되었습니다.';
                loginBtn.textContent = 'Google 계정 로그아웃';
                automationBtn.disabled = false;
                break;
            case 'logged_out':
                authStatus.classList.add('error');
                authStatus.textContent = '로그아웃되었습니다.';
                loginBtn.innerHTML = `<svg aria-hidden="true" class="google-icon" viewBox="0 0 24 24"><path d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z" fill="#4285F4"></path><path d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z" fill="#34A853"></path><path d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l3.66-2.84z" fill="#FBBC05"></path><path d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z" fill="#EA4335"></path><path d="M1 1h22v22H1z" fill="none"></path></svg><span>Google 계정으로 로그인</span>`;
                automationBtn.disabled = true;
                if (isAutomationRunning) handleAutomationToggle();
                break;
            case 'error':
                authStatus.classList.add('error');
                authStatus.textContent = `❌ ${message}`;
                break;
        }
        authStatus.classList.remove('hidden');
    }

    // --- AUTOMATION (PROMPT QUEUE) ---
    function handleAutomationToggle() {
        isAutomationRunning = !isAutomationRunning;
        if (isAutomationRunning) {
            if (!validateInputs(true)) {
                isAutomationRunning = false;
                return;
            }
            document.querySelectorAll('.prompt-item').forEach(item => item.classList.remove('used'));

            automationBtn.textContent = '🛑 자동 포스팅 중지';
            automationBtn.style.backgroundColor = 'var(--error-color)';
            logMessage('▶️', '자동 포스팅 시퀀스를 시작합니다.');
            if (autoShutdownCheckbox.checked) {
                logMessage('🔌', '자동 종료 기능이 활성화되었습니다.');
            }
            runNextInQueue();
        } else {
            automationBtn.textContent = '🚀 자동 포스팅 시작';
            automationBtn.style.backgroundColor = '';
            if (automationTimeoutId) clearTimeout(automationTimeoutId);
            logMessage('⏹️', '자동 포스팅이 사용자에 의해 중지되었습니다.');
            if (shutdownCountdownInterval) {
                cancelShutdown();
            }
        }
    }

    function validateInputs(isAutomation = false) {
        if (!apiKeyInput.value.trim()) { alert('Gemini API 키를 입력해주세요.'); return false; }
        if (isAutomation) {
            const prompts = Array.from(document.querySelectorAll('.prompt-input')).map(input => input.value.trim()).filter(Boolean);
            if (prompts.length === 0) { alert('포스트 주제(프롬프트)를 하나 이상 입력해주세요.'); return false; }
        }
        if (!accessToken) { alert('Google 계정으로 먼저 로그인해주세요.'); return false; }
        const imageSource = document.querySelector('input[name="imageSource"]:checked').value;
        if (imageSource === 'pexels' && !pexelsApiKeyInput.value.trim()) { alert('Pexels API 키를 입력해주세요.'); return false; }
        if (imageSource === 'ai' && !gcpProjectIdInput.value.trim()) { alert('Google Cloud Project ID를 입력해주세요.'); return false; }
        if (imageSource === 'upload' && !uploadedImageUri) { alert('업로드할 이미지를 선택해주세요.'); return false; }
        return true;
    }

    async function runNextInQueue() {
        if (!isAutomationRunning) return;

        const nextPromptItem = document.querySelector('.prompt-item:not(.used)');
        if (!nextPromptItem) {
            logMessage('✅', '모든 프롬프트 처리가 완료되었습니다. 자동 포스팅을 종료합니다.');
            if (autoShutdownCheckbox.checked) {
                initiateShutdown();
            }
            handleAutomationToggle(); // Stop the automation state
            return;
        }
        currentPromptItem = nextPromptItem;
        const topic = currentPromptItem.querySelector('.prompt-input').value.trim();

        if (!topic) {
            currentPromptItem.classList.add('used');
            runNextInQueue();
            return;
        }

        logMessage('ℹ️', `다음 주제 처리 중: "${topic}"`);

        try {
            const imageSource = document.querySelector('input[name="imageSource"]:checked').value;
            const requestBody = {
                apiKey: apiKeyInput.value.trim(),
                topic: topic,
                modelName: geminiModelSelect.value,
                imageSource: imageSource,
                gcpProjectId: gcpProjectIdInput.value.trim(),
                pexelsApiKey: pexelsApiKeyInput.value.trim(),
                accessToken: accessToken,
                tone: writingToneSelect.value,
                audience: targetAudienceInput.value.trim(),
                uploadedImageUri: imageSource === 'upload' ? uploadedImageUri : null
            };

            logMessage('🤖', `백엔드에 콘텐츠 생성을 요청합니다... (모델: ${requestBody.modelName})`);
            approvePostBtn.disabled = true;
            approvePostBtn.textContent = '생성 중...';

            const response = await fetch('http://127.0.0.1:5001/generate-post', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(requestBody)
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(`백엔드 서버 오류 (${response.status}): ${errorData.error || '알 수 없는 오류'}`);
            }

            const data = await response.json();

            if (!data.title || !data.body) {
                 throw new Error("백엔드로부터 콘텐츠를 받았지만 제목이나 본문이 비어있습니다.");
            }
            logMessage('✅', '콘텐츠 수신 완료. 미리보기 창을 확인해주세요.');

            previewTitleInput.value = data.title;
            previewBodyTextarea.value = data.body;
            previewModal.classList.remove('hidden');
            approvePostBtn.disabled = false;
            approvePostBtn.textContent = '승인 및 포스팅';

        } catch (error) {
            logMessage('❌', `[${topic}] 처리 중 오류: ${error.message}`);
            if (autoShutdownCheckbox.checked) {
                logMessage('🔌', '오류가 발생하여 자동 종료를 취소합니다.');
                autoShutdownCheckbox.checked = false; // Prevent future attempts in this run
            }
            currentPromptItem.classList.add('used');
            scheduleNextRun();
        }
    }

    // --- YOUTUBE POST GENERATION ---
    function addYoutubeUrlInput() {
        const item = document.createElement('div');
        item.className = 'youtube-url-item';
        const input = document.createElement('input');
        input.type = 'text';
        input.className = 'youtube-url-input';
        input.placeholder = 'https://www.youtube.com/watch?v=...';
        const removeBtn = document.createElement('button');
        removeBtn.className = 'remove-youtube-url-btn';
        removeBtn.innerHTML = '×';
        item.appendChild(input);
        item.appendChild(removeBtn);
        youtubeUrlContainer.appendChild(item);
        updateYoutubeRemoveButtons();
    }

    function handleYoutubeUrlContainerClick(event) {
        if (event.target.classList.contains('remove-youtube-url-btn')) {
            if (youtubeUrlContainer.children.length > 1) {
                event.target.closest('.youtube-url-item').remove();
            } else {
                event.target.closest('.youtube-url-item').querySelector('input').value = '';
            }
            updateYoutubeRemoveButtons();
        }
    }

    function updateYoutubeRemoveButtons() {
        const items = youtubeUrlContainer.querySelectorAll('.youtube-url-item');
        items.forEach((item) => {
            const btn = item.querySelector('.remove-youtube-url-btn');
            if (btn) {
                btn.style.display = items.length > 1 ? 'block' : 'none';
            }
        });
    }

    async function handleGenerateFromYoutube() {
        logMessage('🎬', 'YouTube 기반 포스트 생성을 시작합니다.');

        if (!validateInputs(false)) {
            logMessage('❌', '오류: 입력값을 확인해주세요.');
            return;
        }

        const urls = Array.from(document.querySelectorAll('.youtube-url-input'))
            .map(input => input.value.trim())
            .filter(url => url.startsWith('http'));

        if (urls.length === 0) {
            alert('하나 이상의 유효한 YouTube URL을 입력해주세요.');
            logMessage('❌', '오류: YouTube URL이 필요합니다.');
            return;
        }

        logMessage('ℹ️', `${urls.length}개의 YouTube URL을 백엔드로 전송합니다.`);

        generateFromYoutubeBtn.disabled = true;
        generateFromYoutubeBtn.textContent = '생성 중...';

        try {
            const imageSource = document.querySelector('input[name="imageSource"]:checked').value;
            const requestBody = {
                urls: urls,
                apiKey: apiKeyInput.value.trim(),
                modelName: geminiModelSelect.value,
                imageSource: imageSource,
                gcpProjectId: gcpProjectIdInput.value.trim(),
                pexelsApiKey: pexelsApiKeyInput.value.trim(),
                accessToken: accessToken,
                tone: writingToneSelect.value,
                audience: targetAudienceInput.value.trim(),
                uploadedImageUri: imageSource === 'upload' ? uploadedImageUri : null
            };

            const response = await fetch('http://127.0.0.1:5001/generate-post-from-youtube', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(requestBody)
            });

            if (!response.ok) {
                const errorData = await response.json();
                throw new Error(`백엔드 서버 오류 (${response.status}): ${errorData.error || '알 수 없는 오류'}`);
            }

            const data = await response.json();
            if (!data.title || !data.body) {
                throw new Error("백엔드로부터 콘텐츠를 받았지만 제목이나 본문이 비어있습니다.");
            }

            logMessage('✅', '콘텐츠 수신 완료. 미리보기 및 포스팅을 진행해주세요.');

            currentPromptItem = null; // 개별 포스팅임을 표시

            previewTitleInput.value = data.title;
            previewBodyTextarea.value = data.body;
            previewModal.classList.remove('hidden');

        } catch (error) {
            logMessage('❌', `YouTube 포스트 생성 중 오류: ${error.message}`);
        } finally {
            generateFromYoutubeBtn.disabled = false;
            generateFromYoutubeBtn.textContent = '🎬 영상으로 포스트 생성';
        }
    }

    // --- MODAL & POSTING LOGIC (MODIFIED) ---
    async function handleApprovePost() {
        const title = previewTitleInput.value;
        const content = previewBodyTextarea.value;
        const isDraft = postAsDraftCheckbox.checked;

        const postData = {
            kind: "blogger#post",
            blog: { id: blogIdInput.value.trim() },
            title: title,
            content: content,
        };

        if (locationInput.value.trim()) {
            postData.location = { name: locationInput.value.trim() };
        }

        try {
            logMessage('🚀', `Blogger에 포스팅을 시작합니다 (${isDraft ? '초안' : '발행'})...`);
            const result = await postToBlogger(postData, isDraft);
            logMessage('🎉', `포스팅 성공! 새 포스트 URL: <a href="${result.url}" target="_blank">${result.url}</a>`);
            addHistoryEntry(title, result.url);
        } catch (error) {
            logMessage('❌', `Blogger 포스팅 실패: ${error.message}`);
             if (autoShutdownCheckbox.checked) {
                logMessage('🔌', '포스팅 오류가 발생하여 자동 종료를 취소합니다.');
                autoShutdownCheckbox.checked = false; // Prevent future attempts in this run
            }
        } finally {
            previewModal.classList.add('hidden');

            if (currentPromptItem) {
                currentPromptItem.classList.add('used');
                scheduleNextRun();
            } else {
                logMessage('✅', '개별 포스팅이 완료되었습니다.');
            }
        }
    }

    function handleCancelPost() {
        previewModal.classList.add('hidden');

        if (currentPromptItem) {
            const topic = currentPromptItem.querySelector('.prompt-input').value.trim() || '빈 주제';
            logMessage('⏭️', `"${topic}" 포스팅을 건너뛰었습니다.`);
            currentPromptItem.classList.add('used');
            if (isAutomationRunning) {
                scheduleNextRun();
            }
        } else {
            logMessage('⏭️', '개별 포스팅 생성을 취소하고 미리보기 창을 닫았습니다.');
        }
    }

    function scheduleNextRun() {
        if (isAutomationRunning) {
            const interval = parseInt(intervalSelect.value, 10);
            logMessage('🕒', `${interval / 1000}초 후 다음 작업을 시작합니다.`);
            automationTimeoutId = setTimeout(runNextInQueue, interval);
        }
    }

    // --- AUTO SHUTDOWN ---
    async function initiateShutdown() {
        try {
            const response = await fetch('http://127.0.0.1:5001/shutdown-pc', { method: 'POST' });
            if (!response.ok) throw new Error('종료 명령 전송 실패');

            logMessage('🔌', 'PC 종료 명령을 전송했습니다. 60초 후에 시스템이 종료됩니다.');
            shutdownStatusContainer.classList.remove('hidden');

            let secondsLeft = 60;
            shutdownStatusText.textContent = `💻 PC가 ${secondsLeft}초 후에 종료됩니다.`;

            shutdownCountdownInterval = setInterval(() => {
                secondsLeft--;
                shutdownStatusText.textContent = `💻 PC가 ${secondsLeft}초 후에 종료됩니다.`;
                if (secondsLeft <= 0) {
                    clearInterval(shutdownCountdownInterval);
                    shutdownStatusContainer.classList.add('hidden');
                }
            }, 1000);

        } catch (error) {
            logMessage('❌', `자동 종료 시작 실패: ${error.message}`);
        }
    }

    async function cancelShutdown() {
        try {
            const response = await fetch('http://127.0.0.1:5001/cancel-shutdown', { method: 'POST' });
            if (!response.ok) throw new Error('종료 취소 명령 전송 실패');

            if (shutdownCountdownInterval) {
                clearInterval(shutdownCountdownInterval);
                shutdownCountdownInterval = null;
            }
            shutdownStatusContainer.classList.add('hidden');
            logMessage('🔌', 'PC 자동 종료가 사용자에 의해 취소되었습니다.');

        } catch (error) {
            logMessage('❌', `자동 종료 취소 실패: ${error.message}`);
        }
    }

    // --- HISTORY MANAGEMENT ---
    function loadHistory() {
        const savedHistory = localStorage.getItem('postingHistory');
        if (savedHistory) postingHistory = JSON.parse(savedHistory);
        renderHistory();
    }

    function saveHistory() {
        localStorage.setItem('postingHistory', JSON.stringify(postingHistory));
    }

    function renderHistory() {
        historyList.innerHTML = '';
        if (postingHistory.length === 0) {
            historyList.innerHTML = '<li class="history-placeholder">아직 포스팅 기록이 없습니다.</li>';
            clearHistoryBtn.style.display = 'none';
        } else {
            postingHistory.forEach(item => {
                const li = document.createElement('li');
                li.className = 'history-item';
                li.innerHTML = `
                    <span class="history-title">${item.title}</span>
                    <div class="history-details">
                        <span class="history-date">${new Date(item.date).toLocaleString('ko-KR')}</span>
                        <a href="${item.url}" target="_blank" class="history-link">포스트 보기</a>
                    </div>`;
                historyList.appendChild(li);
            });
            clearHistoryBtn.style.display = 'inline-flex';
        }
    }

    function addHistoryEntry(title, url) {
        const newEntry = { title, url, date: new Date().toISOString() };
        postingHistory.unshift(newEntry);
        saveHistory();
        renderHistory();
    }

    function handleClearHistory() {
        if (confirm('정말로 모든 포스팅 기록을 삭제하시겠습니까?')) {
            postingHistory = [];
            saveHistory();
            renderHistory();
            logMessage('🗑️', '포스팅 기록이 모두 삭제되었습니다.');
        }
    }

    // --- API CALLS ---
    async function postToBlogger(postData, isDraft = true) {
        const BLOG_ID = blogIdInput.value.trim();
        const API_URL = `https://www.googleapis.com/blogger/v3/blogs/${BLOG_ID}/posts/?isDraft=${isDraft}`;
        const response = await fetch(API_URL, {
            method: 'POST',
            headers: {
                'Authorization': `Bearer ${accessToken}`,
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(postData),
        });
        const result = await response.json();
        if (!response.ok) {
            throw new Error(`Blogger API 오류 (${response.status}): ${result.error?.message || '알 수 없는 오류'}`);
        }
        return result;
    }

    // --- HELPERS ---
    function logMessage(icon, message) {
        const placeholder = logContainer.querySelector('.log-placeholder');
        if (placeholder) placeholder.remove();
        const time = new Date().toLocaleTimeString();
        logContainer.innerHTML += `<p><span class="log-time">[${time}]</span> <span class="log-icon">${icon}</span> ${message}</p>`;
        logContainer.scrollTop = logContainer.scrollHeight;
    }

    // --- KICKSTART THE APP ---
    setInitialTheme();
    loadGisScript();
    loadHistory();
    loadSettings();
    handleImageSourceChange();
    updateYoutubeRemoveButtons();
});
