            } else {
                console.log("[경고] Gemini가 '[IMAGE_HERE]' 플레이스홀더를 생성하지 않았습니다. 이미지를 본문 상단에 추가합니다.");
                const h1EndIndex = generatedText.indexOf('</h1>');
                if (h1EndIndex !== -1) {
                    generatedText = generatedText.slice(0, h1EndIndex + 5) + imageTag + generatedText.slice(h1EndIndex + 5);
                } else {
                    generatedText = imageTag + generatedText;
                }
            }
        }
        console.log(">> Gemini 텍스트 생성 완료!");
        return generatedText;
    } catch (error) {
