// --- [Helper 4] 서버에서 HTML 파싱 ---
function extractDetailsFromServer(htmlContent) {
    let title = 'AI 생성 포스트';
    let body = htmlContent;
    let keywords = '';

    const h1Match = htmlContent.match(/<h1[^>]*>([\s\S]*?)<\/h1>/i);
    if (h1Match && h1Match[1]) {
        title = h1Match[1].trim();
    }

    const keywordsMatch = body.match(/<!--\s*keywords:([\s\S]*?)-->/i);
    if (keywordsMatch && keywordsMatch[1]) {
        keywords = keywordsMatch[1].trim();
    }

    return { title, body, keywords };
}

// --- [Helper 5-A] YouTube 오디오 추출 및 Gemini로 스크립트 변환 (신규) ---
async function transcribeAudioWithGemini(url, apiKey) {
    console.log(`>> YouTube 오디오 스크립트 변환 시도: ${url}`);
    try {
        const info = await ytdl.getInfo(url);
        // Gemini가 지원하는 AAC 코덱을 포함한 m4a 포맷을 우선적으로 찾습니다.
        const audioFormat = ytdl.filterFormats(info.formats, 'audioonly').find(f => f.mimeType.includes('mp4'));
        if (!audioFormat) {
            throw new Error("호환되는 오디오 포맷(m4a/aac)을 찾을 수 없습니다.");
        }
        console.log(`>> 오디오 포맷 발견: ${audioFormat.mimeType}`);

        const audioStream = ytdl(url, { format: audioFormat });
        const chunks = [];
        for await (const chunk of audioStream) {
            chunks.push(chunk);
        }
        const audioBuffer = Buffer.concat(chunks);
        const audioBase64 = audioBuffer.toString('base64');

        console.log(">> 오디오 다운로드 및 Base64 인코딩 완료. Gemini에 전송 중...");

        const genAI = new GoogleGenerativeAI(apiKey);
        const model = genAI.getGenerativeModel({ model: "gemini-1.5-pro-latest" });

        const audioPart = {
            inlineData: {
                mimeType: 'audio/mp4',
                data: audioBase64,
            },
        };

        const prompt = "다음 오디오를 듣고 내용을 글로 작성해주세요. 다른 설명 없이 스크립트 내용만 텍스트로 제공해주세요.";

        const result = await model.generateContent([prompt, audioPart]);
        const response = await result.response;
        const transcript = response.text();

        if (!transcript || transcript.trim().length < 20) { // 너무 짧으면 실패로 간주
             throw new Error("Gemini가 유의미한 스크립트를 생성하지 못했습니다.");
        }

        console.log(">> Gemini 오디오 스크립트 변환 성공!");
        return transcript;

    } catch (error) {
        console.error(`[오류] 오디오 스크립트 변환 중 오류: ${error.message}`);
        return null;
    }
}

// --- [Helper 5-B] YouTube 스크립트 추출 및 결합 (수정됨) ---
async function fetchAndCombineTranscripts(urls, apiKey) {
    console.log(">> YouTube 스크립트 추출 요청 수신...");
    let combinedTranscript = "";
    let videoCount = 0;

    for (const url of urls) {
        if (!url) continue;
        let transcriptText = '';
        try {
            // 1. 기존 방식 (자막/자동 스크립트) 시도
            console.log(`>> [시도 1] 기존 방식으로 스크립트 추출: ${url}`);
            const transcript = await YoutubeTranscript.fetchTranscript(url);
            if (transcript && transcript.length > 0) {
                transcriptText = transcript.map(item => item.text).join(' ');
                console.log(`>> [성공 1] 기존 방식으로 스크립트 추출 완료.`);
            } else {
                throw new Error("기존 스크립트가 비어있습니다.");
            }
        } catch (error) {
            console.warn(`[경고] 기존 방식 실패: ${error.message}. 오디오 직접 변환을 시도합니다.`);
            // 2. 실패 시, 오디오 직접 변환 시도 (Fallback)
            transcriptText = await transcribeAudioWithGemini(url, apiKey);
            if (transcriptText) {
                 console.log(`>> [성공 2] 오디오 직접 변환으로 스크립트 추출 완료.`);
            } else {
                 console.warn(`[경고] URL의 스크립트를 최종적으로 가져올 수 없습니다: ${url}.`);
                 continue; // 이 URL은 건너뛰고 다음으로 넘어감
            }
        }

        if (transcriptText) {
            combinedTranscript += `--- Video ${videoCount + 1} Transcript ---\n${transcriptText}\n\n`;
            videoCount++;
        }
    }
    console.log(`>> 총 ${videoCount}개의 영상에서 스크립트를 성공적으로 추출했습니다.`);
    return combinedTranscript;
}

