}

// --- [Helper 3-1] 데이터 URI를 Gemini가 이해하는 Part로 변환 ---
function dataUriToGenerativePart(uri) {
    const match = uri.match(/^data:(.+);base64,(.+)$/);
    if (!match) throw new Error('Invalid data URI.');
    const [_, mimeType, data] = match;
    return { inlineData: { mimeType, data } };
}

// --- [Helper 3-2] Google Gemini로 텍스트 콘텐츠 생성 (멀티모달 방식) ---
async function generateTextContent(apiKey, topic, imageUrl, modelName, tone, audience) {
    const effectiveModelName = modelName || "gemini-1.5-pro-latest";
    console.log(`>> Gemini 텍스트 생성 요청 수신 (모델: ${effectiveModelName})...`);
    const genAI = new GoogleGenerativeAI(apiKey);
    const model = genAI.getGenerativeModel({ model: effectiveModelName });
    const imagePart = imageUrl ? dataUriToGenerativePart(imageUrl) : null;
    const imageInstruction = imagePart
        ? "제공된 이미지를 분석하고, 블로그 본문 내용 중 가장 자연스럽고 문맥에 맞는 위치에 `[IMAGE_HERE]` 라는 플레이스홀더를 정확히 한 번만 삽입해주세요."
        : "이미지 없음.";

    const basePrompt = `
    당신은 전문적인 SEO 콘텐츠 작가입니다. 다음 지침에 따라 구글 SEO에 매우 최적화된 블로그 게시물을 작성해 주세요.

    **주제:** "${topic}"
    **글쓰기 톤 & 스타일:** ${tone || '지정되지 않음'}
    **타겟 독자:** ${audience || '일반 대중'}

    **요구사항:**
    1.  **HTML 형식:** 모든 결과물은 HTML 태그를 사용하여 작성해 주세요.
    2.  **제목 (H1):** 가장 중요한 키워드를 포함하여 눈길을 끄는 제목을 \`<h1>\` 태그로 만드세요.
    3.  **구조:** 서론, 본론, 결론의 구조를 갖추세요.
    4.  **소제목 (H2/H3):** 본론은 최소 3개 이상의 소주제로 나누고, 각 소주제는 \`<h2>\` 또는 \`<h3>\` 태그를 사용하세요.
    5.  **이미지:** ${imageInstruction}
    6.  **키워드:** 게시물 마지막에 "<!-- keywords: 키워드1, 키워드2 -->" 형식으로 관련 키워드를 5개 이상 포함해주세요. 이 키워드 목록은 HTML 주석 안에 있어야 합니다.
    7.  **독자 고려:** 지정된 '타겟 독자'의 눈높이에 맞춰 용어 선택과 설명의 깊이를 조절해주세요.
    8.  **톤 유지:** 지정된 '글쓰기 톤 & 스타일'을 게시물 전체에 일관되게 유지해주세요.
    `;
    const requestPayload = [basePrompt];
    if (imagePart) requestPayload.push(imagePart);

    try {
        const result = await model.generateContent(requestPayload);
        const response = await result.response;
        let generatedText = response.text();
        if (imageUrl) {
            const imageTag = `<img src="${imageUrl}" alt="${topic}" style="width:100%; height:auto; border-radius:8px; margin-top: 1em; margin-bottom: 1em;">`;
            if (generatedText.includes('[IMAGE_HERE]')) {
                generatedText = generatedText.replace('[IMAGE_HERE]', imageTag);
            } else {
                console.log("[경고] Gemini가 '[IMAGE_HERE]' 플레이스홀더를 생성하지 않았습니다. 이미지를 본문 상단에 추가합니다.");
                const h1EndIndex = generatedText.indexOf('</h1>');
                if (h1EndIndex !== -1) {
                    generatedText = generatedText.slice(0, h1EndIndex + 5) + imageTag + generatedText.slice(h1EndIndex + 5);
                } else {
                    generatedText = imageTag + generatedText;
                }
            }
        }
        console.log(">> Gemini 텍스트 생성 완료!");
        return generatedText;
    } catch (error) {
        console.error(`[오류] Gemini API 호출 중 오류: ${error.message}`);
        throw new Error(`Gemini API 오류: ${error.message}`);
    }
}

// --- [Helper 4] 서버에서 HTML 파싱 ---
function extractDetailsFromServer(htmlContent) {
    let title = 'AI 생성 포스트';
    let body = htmlContent;
    let keywords = '';

    const h1Match = htmlContent.match(/<h1[^>]*>([\s\S]*?)<\/h1>/i);
    if (h1Match && h1Match[1]) {
        title = h1Match[1].trim();
    }

    const keywordsMatch = body.match(/<!--\s*keywords:([\s\S]*?)-->/i);
    if (keywordsMatch && keywordsMatch[1]) {
        keywords = keywordsMatch[1].trim();
    }

    return { title, body, keywords };
}

// --- [Helper 5-A] YouTube 오디오 추출 및 Gemini로 스크립트 변환 (개선됨) ---
async function transcribeAudioWithGemini(url, apiKey) {
    console.log(`>> YouTube 오디오 스크립트 변환 시도: ${url}`);
    try {
        const info = await ytdl.getInfo(url);
        console.log(">> ytdl.getInfo() 성공. 사용 가능한 오디오 포맷 검색 중...");

        // Gemini가 지원하는 포맷 우선순위: m4a (aac), webm (opus)
        const audioFormats = ytdl.filterFormats(info.formats, 'audioonly');

        // 디버깅을 위해 사용 가능한 모든 오디오 포맷을 로그로 남깁니다.
        console.log(">> 사용 가능한 오디오 포맷 목록:", audioFormats.map(f => ({ mimeType: f.mimeType, qualityLabel: f.qualityLabel, itag: f.itag })));

        let audioFormat = audioFormats.find(f => f.mimeType.includes('mp4')) || // m4a (aac)
                          audioFormats.find(f => f.mimeType.includes('webm'));   // webm (opus)

        if (!audioFormat) {
            throw new Error("Gemini와 호환되는 오디오 포맷(m4a, webm)을 찾을 수 없습니다.");
        }

        const mimeType = audioFormat.mimeType.split(';')[0]; // 'audio/mp4; codecs="mp4a.40.2"' -> 'audio/mp4'
        console.log(`>> 최적 오디오 포맷 발견: ${mimeType} (itag: ${audioFormat.itag})`);

        const audioStream = ytdl(url, { format: audioFormat });
        const chunks = [];
        for await (const chunk of audioStream) {
            chunks.push(chunk);
        }
        const audioBuffer = Buffer.concat(chunks);
        const audioBase64 = audioBuffer.toString('base64');

        console.log(`>> 오디오 다운로드 및 Base64 인코딩 완료 (크기: ${(audioBuffer.length / 1024 / 1024).toFixed(2)} MB). Gemini에 전송 중...`);

        const genAI = new GoogleGenerativeAI(apiKey);
        const model = genAI.getGenerativeModel({ model: "gemini-1.5-pro-latest" });

        const audioPart = {
            inlineData: {
                mimeType: mimeType,
                data: audioBase64,
            },
        };

        const prompt = "다음 오디오를 듣고 내용을 글로 작성해주세요. 다른 설명 없이 스크립트 내용만 텍스트로 제공해주세요.";

        const result = await model.generateContent([prompt, audioPart]);
        const response = await result.response;
        const transcript = response.text();

        if (!transcript || transcript.trim().length < 20) { // 너무 짧으면 실패로 간주
             throw new Error("Gemini가 유의미한 스크립트를 생성하지 못했습니다.");
        }

        console.log(">> Gemini 오디오 스크립트 변환 성공!");
        return transcript;

    } catch (error) {
        console.error(`[오류] 오디오 스크립트 변환 중 오류: ${error.message}`);
        if (error.message.includes('No video id found')) {
             console.error("[상세] ytdl이 URL에서 비디오 ID를 찾지 못했습니다. URL 형식을 확인해주세요.");
        } else if (error.message.includes('Status code: 410')) {
             console.error("[상세] YouTube에서 '410 Gone' 오류를 반환했습니다. ytdl-core 라이브러리 업데이트가 필요할 수 있습니다.");
        }
        return null;
    }
}

// --- [Helper 5-B] YouTube 스크립트 추출 및 결합 (수정됨) ---
async function fetchAndCombineTranscripts(urls, apiKey) {
    console.log(">> YouTube 스크립트 추출 요청 수신...");
    let combinedTranscript = "";
    let videoCount = 0;

    for (const url of urls) {
        if (!url) continue;
        let transcriptText = '';
        try {
            // 1. 기존 방식 (자막/자동 스크립트) 시도
            console.log(`>> [시도 1] 기존 방식으로 스크립트 추출: ${url}`);
            const transcript = await YoutubeTranscript.fetchTranscript(url);
            if (transcript && transcript.length > 0) {
                transcriptText = transcript.map(item => item.text).join(' ');
                console.log(`>> [성공 1] 기존 방식으로 스크립트 추출 완료.`);
            } else {
                throw new Error("기존 스크립트가 비어있습니다.");
            }
        } catch (error) {
            console.warn(`[경고] 기존 방식 실패: ${error.message}. 오디오 직접 변환을 시도합니다.`);
            // 2. 실패 시, 오디오 직접 변환 시도 (Fallback)
            transcriptText = await transcribeAudioWithGemini(url, apiKey);
            if (transcriptText) {
                 console.log(`>> [성공 2] 오디오 직접 변환으로 스크립트 추출 완료.`);
            } else {
                 console.warn(`[경고] URL의 스크립트를 최종적으로 가져올 수 없습니다: ${url}.`);
                 continue; // 이 URL은 건너뛰고 다음으로 넘어감
            }
        }

        if (transcriptText) {
            combinedTranscript += `--- Video ${videoCount + 1} Transcript ---\n${transcriptText}\n\n`;
            videoCount++;
        }
    }
    console.log(`>> 총 ${videoCount}개의 영상에서 스크립트를 성공적으로 추출했습니다.`);
    return combinedTranscript;
}

