    if (!apiKey || !topic) {
        return res.status(400).json({ error: "Gemini API 키와 주제는 필수입니다." });
    }

    try {
        let imageUrl = null;
        if (imageSource === 'upload' && uploadedImageUri) {
            console.log(">> 사용자 업로드 이미지 사용...");
