        if (stderr) {
            console.error(`[오류] Shutdown stderr: ${stderr}`);
            return res.status(500).json({ error: `Shutdown command stderr: ${stderr}` });
        }
        console.log(`>> Shutdown stdout: ${stdout}`);
        res.status(200).json({ message: "Shutdown command issued successfully." });
