  <boltAction type="file" filePath="server.js">import express from 'express';
import cors from 'cors';
import bodyParser from 'body-parser';
import axios from 'axios';
import { GoogleGenerativeAI } from '@google/generative-ai';
import { YoutubeTranscript } from 'youtube-transcript';
import { exec } from 'child_process';
import { OAuth2Client } from 'google-auth-library';
import { MongoClient } from 'mongodb';
import dotenv from 'dotenv';

dotenv.config();

// --- 기본 설정 및 DB 연결 ---
const app = express();
const PORT = process.env.PORT || 5001; // Render는 PORT 환경변수를 사용
const mongoClient = new MongoClient(process.env.MONGODB_URI);
let tokensCollection;

async function connectToDb() {
    try {
        await mongoClient.connect();
        const db = mongoClient.db("autoblogger"); // DB 이름
        tokensCollection = db.collection("user_tokens"); // Collection 이름
        console.log("✅ Successfully connected to MongoDB Atlas.");
    } catch (error) {
        console.error("❌ Failed to connect to MongoDB Atlas.", error);
        process.exit(1);
    }
}

// --- OAuth 2.0 클라이언트 설정 ---
const oauth2Client = new OAuth2Client(
    process.env.GOOGLE_CLIENT_ID,
    process.env.GOOGLE_CLIENT_SECRET,
    `${process.env.BACKEND_URL}/auth/google/callback`
);

// --- 미들웨어 설정 ---
app.use(cors());
app.use(bodyParser.json({ limit: '50mb' }));

// --- 인증된 OAuth2 클라이언트 가져오기 (DB 기반) ---
async function getAuthenticatedClient(userId) {
    const userTokens = await tokensCollection.findOne({ userId: userId });
    if (!userTokens || !userTokens.refresh_token) {
        throw new Error('User not authenticated or refresh token missing.');
    }

    const client = new OAuth2Client(process.env.GOOGLE_CLIENT_ID, process.env.GOOGLE_CLIENT_SECRET);
    client.setCredentials(userTokens);

    // Access token 만료 시 자동 갱신
    const expiryDate = userTokens.expiry_date;
    if (expiryDate && (new Date()).getTime() > expiryDate - 60 * 1000) { // 만료 1분 전
        console.log(`>> [${userId}] Access token expiring, refreshing...`);
        const { credentials } = await client.refreshAccessToken();
        client.setCredentials(credentials);
        // 갱신된 토큰 DB에 저장
        await tokensCollection.updateOne(
            { userId: userId },
            { $set: credentials },
            { upsert: true }
        );
        console.log(`>> [${userId}] Token refreshed and saved to DB.`);
    }

    return client;
}

// --- [Helper] 데이터 URI를 Gemini Part로 변환 ---
function dataUriToGenerativePart(uri) {
    const match = uri.match(/^data:(.+);base64,(.+)$/);
    if (!match) throw new Error('Invalid data URI.');
    return { inlineData: { mimeType: match[1], data: match[2] } };
}

// --- [Helper] HTML에서 제목과 키워드 추출 ---
function extractDetailsFromHtml(htmlContent) {
    let title = 'AI 생성 포스트';
    let keywords = '';
    const h1Match = htmlContent.match(/<h1[^>]*>([\s\S]*?)<\/h1>/i);
    if (h1Match && h1Match[1]) title = h1Match[1].trim();
    const keywordsMatch = htmlContent.match(/<!--\s*keywords:([\s\S]*?)-->/i);
    if (keywordsMatch && keywordsMatch[1]) keywords = keywordsMatch[1].trim();
    return { title, body: htmlContent, keywords };
}


// --- 1. 인증 엔드포인트 ---
app.get('/auth/google', (req, res) => {
    const authUrl = oauth2Client.generateAuthUrl({
        access_type: 'offline',
        prompt: 'consent',
        scope: [
            'https://www.googleapis.com/auth/userinfo.profile',
            'https://www.googleapis.com/auth/userinfo.email',
            'https://www.googleapis.com/auth/blogger',
            'https://www.googleapis.com/auth/cloud-platform',
        ],
    });
    res.redirect(authUrl);
});

app.get('/auth/google/callback', async (req, res) => {
    const { code } = req.query;
    try {
        const { tokens } = await oauth2Client.getToken(code);
        oauth2Client.setCredentials(tokens);

        const userInfoResponse = await axios.get('https://www.googleapis.com/oauth2/v2/userinfo', {
            headers: { Authorization: `Bearer ${tokens.access_token}` },
        });
        const userInfo = userInfoResponse.data;
        const userId = `google-${userInfo.id}`;

        // 토큰을 MongoDB에 저장 (Upsert: 없으면 생성, 있으면 업데이트)
        await tokensCollection.updateOne(
            { userId: userId },
            { $set: { ...tokens, userId: userId } },
            { upsert: true }
        );
        console.log(`>> User [${userId}] authenticated and tokens saved to DB.`);

        const redirectUrl = new URL(process.env.FRONTEND_URL);
        redirectUrl.searchParams.set('userId', userId);
        redirectUrl.searchParams.set('userName', userInfo.name);
        redirectUrl.searchParams.set('email', userInfo.email);
        res.redirect(redirectUrl.toString());

    } catch (error) {
        console.error('Error during Google auth callback:', error.message);
        const redirectUrl = new URL(process.env.FRONTEND_URL);
        redirectUrl.searchParams.set('error', 'Authentication failed');
        res.redirect(redirectUrl.toString());
    }
});


// --- 2. 콘텐츠 생성 엔드포인트 ---
async function generateContent(requestBody) {
    const { userId, apiKey, topic, youtubeUrls, imageSource, pexelsApiKey, modelName, tone, audience, uploadedImageUri } = requestBody;

    let imageUrl = null;
    let imagePromptTopic = topic;
    if (youtubeUrls && youtubeUrls.length > 0) {
        const transcripts = await fetchAndCombineTranscripts(youtubeUrls);
        if (!transcripts) throw new Error("스크립트를 추출할 수 없습니다.");
        imagePromptTopic = `A blog post summarizing these YouTube video topics: ${transcripts.substring(0, 250)}`;
        requestBody.transcripts = transcripts;
    }

    if (imageSource === 'upload' && uploadedImageUri) {
        imageUrl = uploadedImageUri;
    } else if (imageSource === 'pexels' && pexelsApiKey) {
        imageUrl = await fetchImageFromPexels(imagePromptTopic, pexelsApiKey);
    } else if (imageSource === 'ai') {
        const client = await getAuthenticatedClient(userId);
        const accessToken = client.credentials.access_token;
        const gcpProjectId = await client.getProjectId();
        imageUrl = await generateAiImage(gcpProjectId, imagePromptTopic, accessToken);
    }

    const htmlContent = await generateTextContent({ ...requestBody, imageUrl });
    return extractDetailsFromHtml(htmlContent);
}

app.post('/generate-post', async (req, res) => {
