// Re-implementing unchanged helpers
fetchImageFromPexels = async function(query, apiKey) {
    const API_URL = `https://api.pexels.com/v1/search?query=${encodeURIComponent(query)}&per_page=15&orientation=landscape`;
    try {
        const pexelsResponse = await axios.get(API_URL, { headers: { 'Authorization': apiKey } });
        if (pexelsResponse.data.photos && pexelsResponse.data.photos.length > 0) {
            const photo = pexelsResponse.data.photos[Math.floor(Math.random() * pexelsResponse.data.photos.length)];
            const imageResponse = await axios.get(photo.src.large2x, { responseType: 'arraybuffer' });
            const mimeType = imageResponse.headers['content-type'] || 'image/jpeg';
            return `data:${mimeType};base64,${Buffer.from(imageResponse.data).toString('base64')}`;
        }
        return null;
    } catch (error) { console.error(`Pexels Error: ${error.message}`); return null; }
};
generateAiImage = async function(projectId, topic, accessToken) {
    const API_ENDPOINT = `https://us-central1-aiplatform.googleapis.com/v1/projects/${projectId}/locations/us-central1/publishers/google/models/imagegeneration@006:predict`;
    const prompt = `A high-quality, photorealistic, cinematic style image representing the concept of '${topic}', suitable for a professional blog post header.`;
    try {
        const response = await axios.post(
            API_ENDPOINT,
            { instances: [{ prompt }], parameters: { sampleCount: 1 } },
            { headers: { 'Authorization': `Bearer ${accessToken}`, 'Content-Type': 'application/json' } }
        );
        const prediction = response.data.predictions[0];
        return `data:image/png;base64,${prediction.bytesBase64Encoded}`;
    } catch (error) { console.error(`Imagen Error: ${error.response?.data?.error?.message || error.message}`); return null; }
};
generateTextContent = async function({ apiKey, topic, transcripts, imageUrl, modelName, tone, audience }) {
    const genAI = new GoogleGenerativeAI(apiKey);
    const model = genAI.getGenerativeModel({ model: modelName || "gemini-1.5-flash-latest" });
    const imagePart = imageUrl ? dataUriToGenerativePart(imageUrl) : null;
    const imageInstruction = imagePart ? "제공된 이미지를 분석하여, 본문 중 가장 자연스러운 위치에 `[IMAGE_HERE]` 플레이스홀더를 한 번만 삽입해주세요." : "이미지 없음.";
    const promptTemplate = `
    당신은 전문 SEO 콘텐츠 작가입니다. 다음 지침에 따라 HTML 형식의 블로그 게시물을 작성해주세요.
    **글쓰기 톤 & 스타일:** ${tone || '친근함'}
    **타겟 독자:** ${audience || '일반 대중'}
    **이미지 지침:** ${imageInstruction}
    **요구사항:**
    1.  **제목(H1):** SEO에 최적화된 눈길을 끄는 제목을 \`<h1>\` 태그로 만드세요.
    2.  **구조:** 서론, 본론, 결론 구조를 갖추고, 본론은 \`<h2>\`, \`<h3>\` 소제목으로 나누세요.
    3.  **키워드:** 게시물 마지막에 "<!-- keywords: 키워드1, 키워드2 -->" 형식으로 관련 키워드 5개를 HTML 주석 안에 포함해주세요.
    4.  **내용:** 아래 제공된 핵심 내용을 기반으로 글을 작성하세요.
    ---
    **핵심 내용:**
    ${transcripts ? `다음 YouTube 스크립트들을 요약 및 재구성:\n${transcripts}` : `주제: "${topic}"`}
    ---
    `;
    const requestPayload = [promptTemplate];
    if (imagePart) requestPayload.push(imagePart);
    const result = await model.generateContent(requestPayload);
    let generatedText = result.response.text();
    if (imageUrl) {
        const imageTag = `<img src="${imageUrl}" alt="${topic || 'Blog post image'}" style="width:100%; height:auto; border-radius:8px; margin: 1em 0;">`;
        if (generatedText.includes('[IMAGE_HERE]')) {
            generatedText = generatedText.replace('[IMAGE_HERE]', imageTag);
        } else {
            const h1EndIndex = generatedText.indexOf('</h1>');
            if (h1EndIndex !== -1) {
                generatedText = generatedText.slice(0, h1EndIndex + 5) + imageTag + generatedText.slice(h1EndIndex + 5);
            } else {
                generatedText = imageTag + generatedText;
            }
        }
    }
    return generatedText;
};
fetchAndCombineTranscripts = async function(urls) {
    let combined = "";
    for (const url of urls) {
        try {
            const transcript = await YoutubeTranscript.fetchTranscript(url);
            if (transcript) combined += transcript.map(item => item.text).join(' ') + '\n\n';
        } catch (error) { console.warn(`Could not fetch transcript for ${url}: ${error.message}`); }
    }
    return combined;
};

// --- 서버 시작 ---
async function startServer() {
    await connectToDb();
    app.listen(PORT, () => {
        console.log(`--- 🚀 AI Auto-Blogging Premium Backend Server Started (Port: ${PORT}) ---`);
        console.log(`--- Frontend should be running on ${process.env.FRONTEND_URL} ---`);
    });
}

startServer();
