// --- [Helper 4] ì„œë²„ì—ì„œ HTML íŒŒì‹± ---
function extractDetailsFromServer(htmlContent) {
    let title = 'AI ìƒì„± í¬ìŠ¤íŠ¸';
    let body = htmlContent;
    let keywords = '';

    const h1Match = htmlContent.match(/<h1[^>]*>([\s\S]*?)<\/h1>/i);
    if (h1Match && h1Match[1]) {
        title = h1Match[1].trim();
    }

    const keywordsMatch = body.match(/<!--\s*keywords:([\s\S]*?)-->/i);
    if (keywordsMatch && keywordsMatch[1]) {
        keywords = keywordsMatch[1].trim();
    }

    return { title, body, keywords };
}

