        if (generatedText.includes('[IMAGE_HERE]')) {
            generatedText = generatedText.replace('[IMAGE_HERE]', imageTag);
        } else {
            const h1EndIndex = generatedText.indexOf('</h1>');
            if (h1EndIndex !== -1) {
                generatedText = generatedText.slice(0, h1EndIndex + 5) + imageTag + generatedText.slice(h1EndIndex + 5);
