                generatedText = imageTag + generatedText;
            }
        }
    }
    return generatedText;
};
fetchAndCombineTranscripts = async function(urls) {
    let combined = "";
    for (const url of urls) {
        try {
            const transcript = await YoutubeTranscript.fetchTranscript(url);
            if (transcript) combined += transcript.map(item => item.text).join(' ') + '\n\n';
        } catch (error) { console.warn(`Could not fetch transcript for ${url}: ${error.message}`); }
    }
    return combined;
};

