/* --- 1. Root & Color Variables --- */
:root {
    --font-sans: 'Inter', -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;

    /* Light Theme */
    --bg-light: #f7f8fa;
    --card-bg-light: #ffffff;
    --text-primary-light: #1a202c;
    --text-secondary-light: #718096;
    --border-light: #e2e8f0;
    --input-bg-light: #ffffff;
    --btn-secondary-bg-light: #f1f5f9;
    --btn-secondary-hover-bg-light: #e2e8f0;
    --summary-hover-bg-light: #f8fafc;

    /* Dark Theme */
    --bg-dark: #121212;
    --card-bg-dark: #1e1e1e;
    --text-primary-dark: #e2e8f0;
    --text-secondary-dark: #a0aec0;
    --border-dark: #2d3748;
    --input-bg-dark: #2d3748;
    --btn-secondary-bg-dark: #2d3748;
    --btn-secondary-hover-bg-dark: #4a5568;
    --summary-hover-bg-dark: #2d3748;

    /* Universal Colors */
    --primary-accent: #3b82f6;
    --primary-gradient: linear-gradient(45deg, #3b82f6, #8b5cf6);
    --primary-gradient-hover: linear-gradient(45deg, #2563eb, #7c3aed);
    --success-color: #10b981;
    --error-color: #ef4444;
    --warning-color: #f97316;
    --box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
    --box-shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
    --border-radius: 0.5rem; /* 8px */
    --transition-fast: all 0.2s ease-in-out;
}

body {
    font-family: var(--font-sans);
    margin: 0;
    padding: 2rem;
    line-height: 1.6;
    background-color: var(--bg-light);
    color: var(--text-primary-light);
    transition: background-color 0.3s, color 0.3s;
}

body[data-theme="dark"] {
    background-color: var(--bg-dark);
    color: var(--text-primary-dark);
}

/* --- 2. Layout & Structure --- */
.container {
    max-width: 1200px;
    margin: 0 auto;
}

header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 2.5rem;
    padding-bottom: 1.5rem;
    border-bottom: 1px solid var(--border-light);
}
body[data-theme="dark"] header { border-bottom-color: var(--border-dark); }

header h1 {
    font-size: 1.875rem; /* 30px */
    font-weight: 700;
    margin: 0;
    display: flex;
    align-items: center;
    gap: 0.75rem;
}

.premium-badge {
    font-size: 0.8rem;
    font-weight: 600;
    background: var(--primary-gradient);
    color: white;
    padding: 0.25rem 0.6rem;
    border-radius: 999px;
    text-transform: uppercase;
    letter-spacing: 0.05em;
}

header p {
    font-size: 1rem;
    color: var(--text-secondary-light);
    margin: 0.25rem 0 0;
}
body[data-theme="dark"] header p { color: var(--text-secondary-dark); }

.main-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(400px, 1fr));
    gap: 1.5rem;
}

.grid-col {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
}

/* --- 3. Card Styles --- */
.card {
    background-color: var(--card-bg-light);
    border-radius: var(--border-radius);
    box-shadow: var(--box-shadow);
    padding: 1.75rem;
    border: 1px solid var(--border-light);
    transition: var(--transition-fast);
}
body[data-theme="dark"] .card {
    background-color: var(--card-bg-dark);
    border-color: var(--border-dark);
}
.card:hover {
    transform: translateY(-3px);
    box-shadow: var(--box-shadow-lg);
}

.card h2 {
    margin-top: 0;
    font-size: 1.25rem; /* 20px */
    font-weight: 600;
    border-bottom: 1px solid var(--border-light);
    padding-bottom: 0.75rem;
    margin-bottom: 1.5rem;
}
body[data-theme="dark"] .card h2 { border-color: var(--border-dark); }

/* --- 4. Form & Button Styles --- */
.form-group { margin-bottom: 1.25rem; }
.form-group:last-child { margin-bottom: 0; }
.form-group label {
    display: block;
    font-weight: 500;
    margin-bottom: 0.5rem;
    font-size: 0.875rem;
}

.form-group input[type="text"],
.form-group input[type="password"],
.form-group select,
.form-group textarea,
.form-group .file-input {
    width: 100%;
    padding: 0.75rem 1rem;
    border: 1px solid var(--border-light);
    border-radius: 0.375rem; /* 6px */
    box-sizing: border-box;
    transition: var(--transition-fast);
    background-color: var(--input-bg-light);
    color: var(--text-primary-light);
    font-size: 1rem;
    font-family: var(--font-sans);
}
body[data-theme="dark"] .form-group input[type="text"],
body[data-theme="dark"] .form-group input[type="password"],
body[data-theme="dark"] .form-group select,
body[data-theme="dark"] .form-group textarea,
body[data-theme="dark"] .form-group .file-input {
    background-color: var(--input-bg-dark);
    border-color: var(--border-dark);
    color: var(--text-primary-dark);
}

.form-group input:focus, .form-group select:focus, .form-group textarea:focus, .form-group .file-input:focus {
    outline: none;
    border-color: var(--primary-accent);
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.2);
}

.form-group .file-input {
    padding: 0.5rem;
}

.checkbox-label {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    cursor: pointer;
}

.button-primary, .button-secondary, .button-google, .button-google-logout {
    padding: 0.75rem 1.25rem;
    border: none;
    border-radius: 0.375rem;
    font-size: 1rem;
    font-weight: 600;
    cursor: pointer;
    transition: var(--transition-fast);
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 0.5rem;
}

.button-primary {
    background: var(--primary-gradient);
    color: white;
    box-shadow: 0 1px 3px 0 rgba(0,0,0,.1), 0 1px 2px 0 rgba(0,0,0,.06);
}
.button-primary:hover {
    background: var(--primary-gradient-hover);
    transform: translateY(-1px);
    box-shadow: var(--box-shadow);
}
.button-primary:disabled {
    background: #9ca3af;
    color: #e5e7eb;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
}

.button-secondary {
    background-color: var(--btn-secondary-bg-light);
    color: var(--text-primary-light);
    border: 1px solid var(--border-light);
}
body[data-theme="dark"] .button-secondary {
    background-color: var(--btn-secondary-bg-dark);
    color: var(--text-primary-dark);
    border-color: var(--border-dark);
}
.button-secondary:hover {
    background-color: var(--btn-secondary-hover-bg-light);
}
body[data-theme="dark"] .button-secondary:hover {
    background-color: var(--btn-secondary-hover-bg-dark);
}

.button-google {
    background-color: #fff;
    color: #374151;
    border: 1px solid #d1d5db;
}
.button-google:hover { background-color: #f9fafb; }
.google-icon { width: 18px; height: 18px; }

.button-google-logout {
    background-color: var(--btn-secondary-bg-light);
    color: var(--text-secondary-light);
    border: 1px solid var(--border-light);
}
body[data-theme="dark"] .button-google-logout {
    background-color: var(--btn-secondary-bg-dark);
    color: var(--text-secondary-dark);
    border-color: var(--border-dark);
}
.button-google-logout:hover {
    background-color: var(--error-color);
    color: white;
    border-color: var(--error-color);
}


.main-actions { display: flex; gap: 1rem; margin-top: 1.5rem; }

/* --- 5. Specific Components --- */

/* Theme Toggle */
.theme-toggle-btn {
    background: var(--btn-secondary-bg-light);
    border: 1px solid var(--border-light);
    width: 44px;
    height: 44px;
    border-radius: 50%;
    cursor: pointer;
    font-size: 1.25rem;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: var(--transition-fast);
    overflow: hidden;
}
body[data-theme="dark"] .theme-toggle-btn {
    background: var(--btn-secondary-bg-dark);
    border-color: var(--border-dark);
}
.theme-toggle-btn:hover { transform: rotate(15deg); }
.icon-moon { display: none; }
body[data-theme="dark"] .icon-sun { display: none; }
body[data-theme="dark"] .icon-moon { display: block; }

/* Settings Details */
.settings-details summary {
    cursor: pointer;
    font-weight: 600;
    padding: 0.75rem 1rem;
    border-radius: 0.375rem;
    transition: var(--transition-fast);
    display: flex;
    align-items: center;
    gap: 0.5rem;
}
.settings-details summary:hover { background-color: var(--summary-hover-bg-light); }
body[data-theme="dark"] .settings-details summary:hover { background-color: var(--summary-hover-bg-dark); }
.settings-details[open] summary {
    background-color: var(--summary-hover-bg-light);
}
body[data-theme="dark"] .settings-details[open] summary {
    background-color: var(--summary-hover-bg-dark);
}
.collapsible-content { padding-top: 1.5rem; display: flex; flex-direction: column; gap: 1.5rem; }

/* Status Box */
.status-box {
    padding: 0.75rem 1rem;
    margin-top: 1.5rem;
    border-radius: 0.375rem;
    text-align: center;
    font-weight: 500;
    font-size: 0.875rem;
}
.status-box.hidden { display: none; }
.status-box.success { background-color: rgba(16, 185, 129, 0.1); color: var(--success-color); }
.status-box.error { background-color: rgba(239, 68, 68, 0.1); color: var(--error-color); }

/* Log Container */
.log-container {
    background-color: var(--bg-dark);
    color: #d1d5db;
    font-family: "SF Mono", "Consolas", "Menlo", monospace;
    font-size: 0.875rem;
    padding: 1rem;
    border-radius: 0.375rem;
    height: 300px;
    overflow-y: auto;
    white-space: pre-wrap;
    word-wrap: break-word;
    border: 1px solid var(--border-dark);
}
.log-placeholder { color: #6b7280; }
.log-time { color: #6b7280; margin-right: 0.5rem; }
.log-icon { margin-right: 0.5rem; }
.log-container a { color: var(--primary-accent); text-decoration: none; }
.log-container a:hover { text-decoration: underline; }

/* Prompt Section */
#promptContainer { display: flex; flex-direction: column; gap: 0.75rem; }
.prompt-item { display: flex; align-items: center; gap: 0.5rem; }
.prompt-item .prompt-input { flex-grow: 1; }
.prompt-item.used .prompt-input {
    text-decoration: line-through;
    color: var(--text-secondary-light);
    background-color: var(--btn-secondary-bg-light);
}
body[data-theme="dark"] .prompt-item.used .prompt-input {
    color: var(--text-secondary-dark);
    background-color: var(--btn-secondary-bg-dark);
}
.remove-prompt-btn {
    background-color: var(--btn-secondary-bg-light);
    color: var(--text-secondary-light);
    border: 1px solid var(--border-light);
    border-radius: 50%;
    width: 28px;
    height: 28px;
    font-size: 16px;
    cursor: pointer;
    flex-shrink: 0;
    transition: var(--transition-fast);
}
body[data-theme="dark"] .remove-prompt-btn {
    background-color: var(--btn-secondary-bg-dark);
    color: var(--text-secondary-dark);
    border-color: var(--border-dark);
}
.remove-prompt-btn:hover { background-color: #ef4444; color: white; border-color: #ef4444; }

/* Image Source Selector */
.image-source-selector {
    display: flex;
    flex-wrap: wrap;
    gap: 1rem 1.5rem;
    background-color: var(--summary-hover-bg-light);
    padding: 1rem;
    border-radius: 0.375rem;
    border: 1px solid var(--border-light);
}
body[data-theme="dark"] .image-source-selector {
    background-color: var(--summary-hover-bg-dark);
    border-color: var(--border-dark);
}
.image-source-selector label {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    cursor: pointer;
    margin-bottom: 0;
}

.info-text {
    font-size: 0.875rem;
    color: var(--text-secondary-light);
    background-color: var(--summary-hover-bg-light);
    padding: 0.75rem;
    border-radius: 0.375rem;
    border-left: 3px solid var(--primary-accent);
}
body[data-theme="dark"] .info-text {
    color: var(--text-secondary-dark);
    background-color: var(--summary-hover-bg-dark);
}

/* Image Upload Preview */
.image-preview-container {
    margin-top: 1rem;
    position: relative;
    width: 100%;
    max-width: 200px;
    border: 1px solid var(--border-light);
    border-radius: 0.375rem;
    padding: 0.5rem;
}
body[data-theme="dark"] .image-preview-container {
    border-color: var(--border-dark);
}
#imagePreview {
    width: 100%;
    height: auto;
    display: block;
    border-radius: 0.25rem;
}
.remove-image-btn {
    position: absolute;
    top: -10px;
    right: -10px;
    background-color: var(--error-color);
    color: white;
    border: 2px solid var(--card-bg-light);
    border-radius: 50%;
    width: 24px;
    height: 24px;
    font-size: 14px;
    line-height: 20px;
    text-align: center;
    cursor: pointer;
    transition: var(--transition-fast);
}
body[data-theme="dark"] .remove-image-btn {
    border-color: var(--card-bg-dark);
}
.remove-image-btn:hover {
    transform: scale(1.1);
}


/* YouTube Post Section */
.card-description {
    font-size: 0.875rem;
    color: var(--text-secondary-light);
    margin-top: -1rem;
    margin-bottom: 1.5rem;
}
body[data-theme="dark"] .card-description {
    color: var(--text-secondary-dark);
}

#youtubeUrlContainer { display: flex; flex-direction: column; gap: 0.75rem; }
.youtube-url-item { display: flex; align-items: center; gap: 0.5rem; }
.youtube-url-item .youtube-url-input { flex-grow: 1; }
.remove-youtube-url-btn {
    background-color: var(--btn-secondary-bg-light);
    color: var(--text-secondary-light);
    border: 1px solid var(--border-light);
    border-radius: 50%;
    width: 28px;
    height: 28px;
    font-size: 16px;
    cursor: pointer;
    flex-shrink: 0;
    transition: var(--transition-fast);
}
body[data-theme="dark"] .remove-youtube-url-btn {
    background-color: var(--btn-secondary-bg-dark);
    color: var(--text-secondary-dark);
    border-color: var(--border-dark);
}
.remove-youtube-url-btn:hover { background-color: #ef4444; color: white; border-color: #ef4444; }


/* History Section */
.history-controls { display: flex; justify-content: flex-end; margin-bottom: 1rem; }
.history-list { list-style: none; padding: 0; margin: 0; display: flex; flex-direction: column; gap: 1rem; }
.history-item {
    background-color: transparent;
    padding: 1rem;
    border-radius: 0.375rem;
    border: 1px solid var(--border-light);
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 1rem;
    transition: var(--transition-fast);
}
body[data-theme="dark"] .history-item { border-color: var(--border-dark); }
.history-item:hover {
    background-color: var(--summary-hover-bg-light);
    border-color: var(--primary-accent);
}
body[data-theme="dark"] .history-item:hover {
    background-color: var(--summary-hover-bg-dark);
}
.history-title { font-weight: 500; flex-grow: 1; }
.history-details { display: flex; align-items: center; gap: 1rem; flex-shrink: 0; }
.history-date { font-size: 0.875rem; color: var(--text-secondary-light); }
body[data-theme="dark"] .history-date { color: var(--text-secondary-dark); }
.history-link {
    background-color: var(--primary-accent);
    color: white;
    padding: 0.375rem 0.75rem;
    border-radius: 0.25rem;
    text-decoration: none;
    font-size: 0.875rem;
    font-weight: 500;
    transition: var(--transition-fast);
}
.history-link:hover { background-color: #2563eb; }
.history-placeholder {
    text-align: center;
    color: var(--text-secondary-light);
    padding: 2rem;
    list-style: none;
    border: 2px dashed var(--border-light);
    border-radius: var(--border-radius);
}
body[data-theme="dark"] .history-placeholder {
    color: var(--text-secondary-dark);
    border-color: var(--border-dark);
}

/* --- 6. Modal Styles --- */
.modal-backdrop {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.6);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 1000;
    backdrop-filter: blur(5px);
}
.modal-content {
    background-color: var(--card-bg-light);
    padding: 2rem;
    border-radius: var(--border-radius);
    box-shadow: var(--box-shadow-lg);
    width: 90%;
    max-width: 800px;
    max-height: 90vh;
    display: flex;
    flex-direction: column;
    border: 1px solid var(--border-light);
}
body[data-theme="dark"] .modal-content {
    background-color: var(--card-bg-dark);
    border-color: var(--border-dark);
}
.modal-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    border-bottom: 1px solid var(--border-light);
    padding-bottom: 1rem;
    margin-bottom: 1.5rem;
}
body[data-theme="dark"] .modal-header { border-bottom-color: var(--border-dark); }
.modal-header h2 { margin: 0; padding: 0; border: none; }
.modal-close-btn {
    background: none;
    border: none;
    font-size: 1.75rem;
    cursor: pointer;
    color: var(--text-secondary-light);
}
body[data-theme="dark"] .modal-close-btn { color: var(--text-secondary-dark); }
.modal-body { overflow-y: auto; }
.modal-footer {
    display: flex;
    justify-content: flex-end;
    gap: 1rem;
    padding-top: 1.5rem;
    margin-top: 1.5rem;
    border-top: 1px solid var(--border-light);
}
body[data-theme="dark"] .modal-footer { border-top-color: var(--border-dark); }

/* --- 7. Responsive Design --- */
@media (max-width: 768px) {
    body { padding: 1rem; }
    header { flex-direction: column; align-items: flex-start; gap: 1rem; }
    .main-grid { grid-template-columns: 1fr; }
    .history-item { flex-direction: column; align-items: flex-start; }
    .history-details { width: 100%; justify-content: space-between; margin-top: 0.75rem; }
    .image-source-selector { flex-direction: column; gap: 1rem; }
}

/* --- 8. Utility & New Feature Styles --- */
.hidden {
    display: none !important;
}

.divider {
    border: none;
    border-top: 1px solid var(--border-light);
    margin: 1.5rem 0;
}
body[data-theme="dark"] .divider {
    border-top-color: var(--border-dark);
}

.warning-text {
    font-size: 0.8rem;
    color: var(--warning-color);
    margin-top: 0.5rem;
    padding: 0.5rem;
    background-color: rgba(249, 115, 22, 0.05);
    border-radius: 0.25rem;
    border-left: 3px solid var(--warning-color);
}

.shutdown-status-container {
    background-color: rgba(249, 115, 22, 0.1);
    border: 1px solid var(--warning-color);
    color: var(--warning-color);
    padding: 1rem;
    border-radius: var(--border-radius);
    display: flex;
    justify-content: space-between;
    align-items: center;
    font-weight: 600;
}
.shutdown-status-container p {
    margin: 0;
}
.shutdown-status-container .button-secondary {
    border-color: var(--warning-color);
    color: var(--warning-color);
}
.shutdown-status-container .button-secondary:hover {
    background-color: var(--warning-color);
    color: white;
}
